mapper.update:

UPDATE bs_book

SET bk_stock = bk_stock + #{stockAdd}

WHERE bk_num = #{bkNum}


UPDATE bs_order

SET od_dv = #{cNum}

WHERE od_num = #{oNum}

mapper.select:
SELECT bk_num, bk_name, bk_price, bk_pictureUrl, bk_thumbUrl

FROM bs_book

WHERE bk_num = #{bk_num}

mapper.update:
UPDATE bs_book

SET bk_sellcnt = bk_sellcnt + #{bkOdcnt}

WHERE bk_num = #{bkNum}

UPDATE bs_book

SET bk_stock = bk_stock - #{bkOdcnt}

WHERE bk_num = #{bkNum}

UPDATE bs_user

SET user_point = #{uPoint}

WHERE user_id = #{userID}

UPDATE bs_user

SET user_rank = 1

WHERE user_id = #{userID}

UPDATE bs_user

SET user_rank = 2

WHERE user_id = #{userID}
